function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),t=document.querySelector(".navbar"),i=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),t.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")):(s.classList.add("active"),t.classList.add("active"),e.classList.add("active-burger"),i.classList.add("locked"))}));document.querySelectorAll(".menu__item-link, .menu__subitem-link").forEach((c=>{c.addEventListener("click",(function(){s.classList.remove("active"),t.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),t.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function sublistView(){const e=document.querySelectorAll("[data-menu-item]");e.forEach((s=>{const t=s.querySelector("[data-menu-item-btn]");s.querySelector("[data-menu-item-sublist]");t.addEventListener("click",(()=>{s.classList.contains("open")?s.classList.remove("open"):(e.forEach((e=>{e.classList.remove("open")})),s.classList.add("open"))}))}))}function newsSlider(){if(!document.querySelector(".news"))return null;new Swiper(".news__slider",{slidesPerView:1,spaceBetween:10,pagination:{el:".news__slider-pagination",type:"bullets",clickable:!0},breakpoints:{320:{slidesPerView:1,spaceBetween:20},767:{slidesPerView:2.5,spaceBetween:20},1200:{slidesPerView:3.5,spaceBetween:40}}})}burgerMenu(),window.addEventListener("scroll",fixedNav),sublistView(),newsSlider();